DECLARE @myDoc xml;\nDECLARE @ProdID int;\nSET @myDoc = '<Root>\n<ProductDescription ProductID="1" ProductName="Road Bike">\n<Features>\n  <Warranty>1 year parts and labor</Warranty>\n  <Maintenance>3 year parts and labor extended maintenance is available</Maintenance>\n</Features>\n</ProductDescription>\n</Root>';\n\n-- Retrieve the Warranty text\nDECLARE @WarrantyText nvarchar(255);\nSET @WarrantyText = @myDoc.value('(/Root/ProductDescription/Features/Warranty/text())[1]', 'nvarchar(255)');\n\nSELECT @WarrantyText AS Warranty;