-- Define and open the cursor\nDECLARE c_Temp CURSOR FOR\n    SELECT name FROM employees;\n\nDECLARE @name varchar(100);\n\nOPEN c_Temp;\n\n-- Fetch data into variable and process it\nFETCH NEXT FROM c_Temp INTO @name;\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    PRINT @name; -- Use the fetched data\n    FETCH NEXT FROM c_Temp INTO @name; -- Fetch next row\nEND\n\n-- Close and deallocate cursor to free resources\nCLOSE c_Temp;\nDEALLOCATE c_Temp;