-- Sample Data Table\nDECLARE @tbl_Info TABLE (Info VARCHAR(10))\n\nINSERT INTO @tbl_Info (Info)\nVALUES ('Name1'), ('Age1'), ('Sex1'),\n       ('Name2'), ('Age2'), ('Sex2')\n\n-- Row-Col Transposition using CASE and ROW_NUMBER\nSET NOCOUNT ON\n\nDECLARE @TempTable TABLE (\n    RowNum INT,\n    Name VARCHAR(10),\n    Age VARCHAR(10),\n    Sex VARCHAR(10)\n)\n\nDECLARE @RowCounter INT\nSELECT @RowCounter = 0\n\nINSERT INTO @TempTable (RowNum, Name, Age, Sex)\nSELECT \n    ROW_NUMBER() OVER (PARTITION BY LEFT(Info, LEN(Info) - 1) ORDER BY Info) AS RowNum,\n    MAX(CASE WHEN LEFT(Info, LEN(Info) - 1) = 'Name' THEN Info END) AS Name,\n    MAX(CASE WHEN LEFT(Info, LEN(Info) - 1) = 'Age' THEN Info END) AS Age,\n    MAX(CASE WHEN LEFT(Info, LEN(Info) - 1) = 'Sex' THEN Info END) AS Sex\nFROM @tbl_Info\nGROUP BY \n    (ROW_NUMBER() OVER (PARTITION BY LEFT(Info, LEN(Info) - 1) ORDER BY Info) - CASE LEFT(Info, LEN(Info) - 1)\n        WHEN 'Name' THEN 0\n        WHEN 'Age' THEN 1\n        WHEN 'Sex' THEN 2\n    END)\n\nSELECT Name, Age, Sex FROM @TempTable