CREATE OR REPLACE PROCEDURE Get_Type_One_Polygon_Values(in_role VARCHAR2, rc_generic OUT SYS_REFCURSOR) AS\n    v_sql VARCHAR2(1000);\nBEGIN\n    -- Construct the dynamic SQL query based on the input parameter\n    v_sql := 'SELECT ' || in_role || '_POLY_ID, ' || in_role || '_POLY_NAME ' ||\n             'FROM TEAMCHK.' || in_role;\n\n    -- Open the cursor with the constructed SQL statement\n    OPEN rc_generic FOR v_sql;\nEND Get_Type_One_Polygon_Values;