-- Ensure there is a unique constraint on the column to prevent duplicates during cleanup\nALTER TABLE YourTable ADD CONSTRAINT UK_YourColumn UNIQUE (YourColumn);\n\n-- Assuming your table and primary key column are named 'YourTable' and 'YourColumn'\nBEGIN TRANSACTION;\n\n-- Drop existing PK if it exists\nIF EXISTS (SELECT * FROM sys.key_constraints WHERE type = 'PK' AND parent_object_id = OBJECT_ID('YourTable'))\n    ALTER TABLE YourTable DROP CONSTRAINT PK_YourColumn;\n\n-- Create a new identity column\nALTER TABLE YourTable ADD NewIdentity INT IDENTITY(1, 1) NOT NULL;\n\n-- Add the primary key constraint back to the new identity column\nALTER TABLE YourTable ADD CONSTRAINT PK_NewIdentity PRIMARY KEY CLUSTERED (NewIdentity);\n\n-- Optionally, you can drop the old column if it is no longer needed and replace with the identity\n-- ALTER TABLE YourTable DROP COLUMN YourColumn;\n-- EXEC sp_rename 'YourTable.NewIdentity', 'YourColumn', 'COLUMN';\n\nCOMMIT TRANSACTION;\n\n-- Clean up: Drop the unique constraint if no longer needed\nALTER TABLE YourTable DROP CONSTRAINT UK_YourColumn;