-- Find all foreign key constraints referencing the 'address' table in PostgreSQL\nSELECT \n    tc.table_name, \n    kcu.column_name \nFROM \n    information_schema.table_constraints AS tc \nJOIN \n    information_schema.key_column_usage AS kcu ON tc.constraint_name = kcu.constraint_name\nWHERE \n    tc.constraint_type = 'FOREIGN KEY' AND \n    tc.table_catalog = 'your_database_name' AND  -- replace with your database name\n    kcu.referenced_table_name = 'address';