SELECT (CASE \n            WHEN t.freeplayabandoned <> f.freeplayabandoned THEN 'freeplayabandoned'\n            WHEN t.freeplaydownloaded <> f.freeplaydownloaded THEN 'freeplaydownloaded'\n        END) AS ColumnNameDifference\nFROM testtable t\nWHERE NOT EXISTS (\n    SELECT 1\n    FROM freeplay.egmfreeplay f\n    WHERE f.freeplaydownloaded = t.freeplaydownloaded\n      AND f.freeplayabandoned = t.freeplayabandoned\n)