-- Create a temp table with the structure you want\nCREATE TABLE dbo.NewTable (\n    NewCol INT IDENTITY(1,1) PRIMARY KEY,\n    -- Add other columns here\n);\n\n-- Copy data from old to new table if needed\nINSERT INTO dbo.NewTable (-- Column list excluding identity column)\nSELECT -- Columns from existing table excluding identity column\nFROM dbo.OldTable;\n\n-- Drop the original table\nDROP TABLE dbo.OldTable;\n\n-- Rename the temp table to the original table name\nEXEC sp_rename 'dbo.NewTable', 'OldTable';