CREATE OR REPLACE PACKAGE BODY B AS\n\n  PROCEDURE my_procedure IS\n    call_stack VARCHAR2(4000);\n  BEGIN\n    -- Capture the call stack\n    call_stack := DBMS_UTILITY.FORMAT_CALL_STACK;\n\n    -- Output or log the call stack (for demonstration purposes)\n    DBMS_OUTPUT.PUT_LINE('Call Stack:');\n    DBMS_OUTPUT.PUT_LINE(call_stack);\n\n    -- Here you could parse the call_stack to see if it includes package A\n  END my_procedure;\n\nEND B;