SET @prefix = 'SitePrefix_';  -- Replace 'SitePrefix_' with your actual prefix\n\nSELECT \n    TABLE_SCHEMA,\n    TABLE_NAME,\n    TABLE_TYPE\nFROM INFORMATION_SCHEMA.TABLES\nWHERE TABLE_NAME LIKE CONCAT(@prefix, '%')\nUNION ALL\nSELECT \n    ROUTINE_SCHEMA,\n    ROUTINE_NAME,\n    ROUTINE_TYPE\nFROM INFORMATION_SCHEMA.ROUTINES\nWHERE ROUTINE_NAME LIKE CONCAT(@prefix, '%');