DO $$\nDECLARE \n    prefix TEXT := 'SitePrefix_';  -- Replace 'SitePrefix_' with your actual prefix\nBEGIN\n    RAISE NOTICE '%',\n        (SELECT string_agg(format('Schema: %s, Name: %s, Type: %s', \n                                  n.nspname, c.relname, c.relkind::regtype::text), E'\n')\n         FROM pg_class c\n                  JOIN pg_namespace n ON n.oid = c.relnamespace\n         WHERE c.relname LIKE prefix || '%'\n           AND n.nspname NOT IN ('pg_catalog', 'information_schema'));\nEND $$;