BEGIN\n   IF NOT EXISTS (SELECT 1 \n                  FROM USER_INDEXES \n                  WHERE INDEX_NAME = 'MYINDEX') THEN\n       EXECUTE IMMEDIATE 'CREATE INDEX MYINDEX ON MYTABLE(MYCOLUMN)';\n   END IF;\nEXCEPTION\n   WHEN OTHERS THEN\n      DBMS_OUTPUT.PUT_LINE('An error occurred: ' || SQLERRM);\nEND;\n/