WITH RECURSIVE OrganizationHierarchy AS (\n    -- Base case: select the topmost organization by ID.\n    SELECT OrganizationId, Name, ParentId\n    FROM Organizations\n    WHERE OrganizationId = ? -- Replace this with your target parent Org ID\n    \n    UNION ALL\n    \n    -- Recursive Part: get the children of the previously selected organizations.\n    SELECT o.OrganizationId, o.Name, o.ParentId\n    FROM Organizations o\n    INNER JOIN OrganizationHierarchy oh ON oh.OrganizationId = o.ParentId\n)\nSELECT OrganizationId\nFROM OrganizationHierarchy;