WITH RECURSIVE BlockSequence AS (\n  SELECT CAST(MIN(start_block) AS UNSIGNED) AS block_number FROM your_table_name UNION ALL\n  SELECT block_number + 1 \n  FROM BlockSequence, your_table_name t\n  WHERE block_number < (SELECT MAX(end_block) FROM your_table_name)\n)\n, ExpectedBlocks AS (\n  SELECT DISTINCT block_number \n  FROM BlockSequence bs\n)\n, ActualBlocks AS (\n  SELECT start_block - 1 AS block_number \n  FROM your_table_name\n  UNION ALL\n  SELECT end_block \n  FROM your_table_name\n)\nSELECT DISTINCT eb.block_number\nFROM ExpectedBlocks eb\nLEFT JOIN ActualBlocks ab ON eb.block_number = ab.block_number\nWHERE ab.block_number IS NULL\nORDER BY eb.block_number;