DECLARE\n     v_table_name VARCHAR2(30) := 'FOO'; -- your table name here\n     v_sql          VARCHAR2(4000);\n     CURSOR cur_columns IS\n       SELECT column_name\n         FROM all_tab_columns\n        WHERE table_name = UPPER(v_table_name)\n          AND data_type NOT IN ('BLOB', 'CLOB'); -- Exclude BLOB and CLOB types\n   BEGIN\n     v_sql := 'SELECT ';\n     \n     FOR rec_col IN cur_columns LOOP\n       IF v_sql IS NOT NULL THEN\n         v_sql := v_sql || ', ';\n       END IF;\n       v_sql := v_sql || rec_col.column_name;\n     END LOOP;\n\n     v_sql := v_sql || ' FROM ' || v_table_name;\n\n     DBMS_OUTPUT.PUT_LINE(v_sql);\n   END;