CREATE TABLE Blogs (\n    ID INT PRIMARY KEY AUTO_INCREMENT,\n    Name VARCHAR(255),\n    Owner VARCHAR(255)\n);\n\nCREATE TABLE Categories (\n    ID INT PRIMARY KEY AUTO_INCREMENT,\n    Name VARCHAR(255) UNIQUE -- Ensure category names are unique\n);\n\n-- Junction table to handle many-to-many relationship\nCREATE TABLE BlogCategories (\n    BlogID INT,\n    CategoryID INT,\n    PRIMARY KEY (BlogID, CategoryID),\n    FOREIGN KEY (BlogID) REFERENCES Blogs(ID) ON DELETE CASCADE,\n    FOREIGN KEY (CategoryID) REFERENCES Categories(ID)\n);\n\nCREATE TABLE Articles (\n    ID INT PRIMARY KEY AUTO_INCREMENT,\n    BlogID INT,\n    Name VARCHAR(255),\n    Content TEXT,\n    FOREIGN KEY (BlogID) REFERENCES Blogs(ID) ON DELETE SET NULL -- Soft delete handling\n);