select \n    Count(Page) as VisitingCount,\n    Page,\n    CONVERT(VARCHAR(5), Date, 108) AS Time_HHMM -- Changed VARCHAR(8) to VARCHAR(5)\nfrom \n    scr_SecuristLog  \nwhere \n    Date between '2009-05-04 00:00:00' and '2009-05-06 14:58'  \n    and [user] in (\n        select USERNAME             \n        from scr_CustomerAuthorities \n    )  \ngroup by \n    Page, CONVERT(VARCHAR(5), Date, 108) -- Group by the converted time\norder by \n    [VisitingCount] asc