SELECT \n    d.DataId, \n    (SELECT [Name] FROM Category WHERE CategoryId = d.CategoryId) AS 'Category', \n    d.Description, \n    d.CompanyName, \n    d.City, \n    d.CategoryId,\n    d.CreatedOn, \n    d.Rank, \n    d.voteCount, \n    d.commentCount, \n    d.viewCount\nFROM Data d \nINNER JOIN Keyword k ON d.DataId = k.DataId\nWHERE FREETEXT(k.Keyword, @SearchQ) \n  AND d.CategoryId=@CategoryId \n  AND d.IsSearch=1 \n  AND d.IsApproved=1\nORDER BY \n    CASE WHEN k.Keyword LIKE '%' + @SearchQ + '%' THEN 0 ELSE 1 END,\n    CONTAINSTABLE(Data, (Description, CompanyName), '"' + @SearchQ + '"') AS RankDesc