CREATE TABLE essays (\n    essay_id INT PRIMARY KEY AUTO_INCREMENT,\n    content TEXT NOT NULL,\n    -- other fields as necessary\n);\n\nCREATE TABLE rebuttals (\n    rebuttal_id INT PRIMARY KEY AUTO_INCREMENT,\n    essay_id INT NOT NULL,\n    author_id INT NOT NULL,\n    response_number TINYINT CHECK (response_number >= 1 AND response_number <= 2),\n    content VARCHAR(2000) CHECK (LENGTH(content) <= 2000 AND response_number = 1) \n                            OR CHECK (LENGTH(content) <= 500 AND response_number = 2),\n    timestamp DATETIME DEFAULT CURRENT_TIMESTAMP,\n    FOREIGN KEY (essay_id) REFERENCES essays(essay_id),\n    UNIQUE (essay_id, response_number) -- Ensures only two rebuttals per essay\n);\n\nCREATE TABLE comments (\n    comment_id INT PRIMARY KEY AUTO_INCREMENT,\n    essay_id INT NOT NULL,\n    author_id INT NOT NULL,\n    content TEXT CHECK (LENGTH(content) <= 500), -- Assuming n = 500 for simplicity\n    timestamp DATETIME DEFAULT CURRENT_TIMESTAMP,\n    FOREIGN KEY (essay_id) REFERENCES essays(essay_id)\n);