DECLARE\n    l_xml XMLTYPE := XMLTYPE('<?xml version="1.0"?><users><user><name>user1</name></user><user><name>user2</name></user><user><name>user3</name></user></users>');\n    l_name VARCHAR2(50);\nBEGIN\n    FOR rec IN (SELECT extractvalue(value(xml), '/user/name') AS name\n                FROM TABLE(XMLSEQUENCE(EXTRACT(l_xml, '//user'))) xml)\n    LOOP\n        l_name := rec.name;\n        DBMS_OUTPUT.PUT_LINE('User Name: ' || l_name);\n    END LOOP;\nEND;\n/