-- Simple SQL query\nSELECT * FROM Employees WHERE Department = 'Sales';\n\n-- PL/SQL block: Create a procedure to calculate average salary in Oracle\nCREATE OR REPLACE PROCEDURE AverageSalary(dept IN VARCHAR2, avg_salary OUT NUMBER) AS\nBEGIN\n    SELECT AVG(Salary) INTO avg_salary FROM Employees WHERE Department = dept;\nEND;\n\n-- T-SQL sample: A stored procedure for the same task in SQL Server\nCREATE PROCEDURE dbo.CalculateAverageSalary @Department NVARCHAR(50), @AvgSalary DECIMAL(18, 2) OUTPUT\nAS\nBEGIN\n    SELECT @AvgSalary = AVG(Salary) FROM Employees WHERE Department = @Department;\nEND;