-- Assuming PostgreSQL as the RDBMS\nCREATE OR REPLACE FUNCTION pivot_data()\nRETURNS TABLE(\n    id INT,\n    type10 INT,\n    type14 INT,\n    type25 INT -- Add more fields if necessary\n) LANGUAGE plpgsql AS $$\nBEGIN\n    RETURN QUERY\n        SELECT \n            id,\n            MAX(CASE WHEN Type = 10 THEN Value END) AS type10,\n            MAX(CASE WHEN Type = 14 THEN Value END) AS type14,\n            MAX(CASE WHEN Type = 25 THEN Value END) AS type25\n        FROM vertical_table\n        GROUP BY id;\nEND;\n$$;\n\n-- Call the function to get the pivoted data\nSELECT * FROM pivot_data();