DECLARE @datePart VARCHAR(2)\nDECLARE @dateParameter INT\n\nSELECT @datePart = 'dd'\nSELECT @dateParameter = 28\n\nSELECT *\nFROM MyTable\nWHERE \n    CASE \n        WHEN @datePart = 'yy' THEN DATEDIFF(yy, MyTable.MyDate, GETDATE())\n        WHEN @datePart = 'qq' THEN DATEDIFF(qq, MyTable.MyDate, GETDATE())\n        WHEN @datePart = 'mm' THEN DATEDIFF(mm, MyTable.MyDate, GETDATE())\n        WHEN @datePart = 'dd' THEN DATEDIFF(dd, MyTable.MyDate, GETDATE())\n        WHEN @datePart = 'hh' THEN DATEDIFF(hh, MyTable.MyDate, GETDATE())\n        WHEN @datePart = 'mi' THEN DATEDIFF(mi, MyTable.MyDate, GETDATE())\n        WHEN @datePart = 'ss' THEN DATEDIFF(ss, MyTable.MyDate, GETDATE())\n        ELSE 0\n    END < @dateParameter