-- Create the table with an auto-increment primary key\nCREATE TABLE bkPhone (\n    phoneId INT AUTO_INCREMENT PRIMARY KEY,\n    phoneNo VARCHAR(20),\n    firstName VARCHAR(20),\n    lastName VARCHAR(20)\n);\n\n-- Insert sample data, including duplicates\nINSERT INTO bkPhone (phoneNo, firstName, lastName) VALUES \n('0783313780', 'Brady', 'Kelly'),\n('0845319792', 'Mark', 'Smith'),\n('0834976958', 'Bill', 'Jones'),\n('0845319792', 'Mark', 'Smith'),\n('0828329792', 'Mickey', 'Mouse'),\n('0834976958', 'Bill', 'Jones');\n\n-- Delete duplicates, keeping the row with the smallest phoneId\nDELETE FROM bkPhone \nWHERE phoneId NOT IN (\n    SELECT MIN(phoneId)\n    FROM bkPhone\n    GROUP BY phoneNo\n);