BEGIN TRANSACTION\nDELETE table_y\n\n-- Example query with RECOMPILE option\nINSERT INTO table_y \nSELECT * FROM (\n    SELECT col1, col2 FROM view_a WHERE ...\n    UNION ALL\n    SELECT col1, col2 FROM view_b WHERE ...\n    UNION ALL\n    SELECT col1, col2 FROM view_c WHERE ...\n) AS CombinedResults OPTION (RECOMPILE)\n\nUPDATE table_x SET last_reprocessed = GETDATE()\nCOMMIT TRANSACTION