-- Users table\nCREATE TABLE users (\n    user_id INT PRIMARY KEY AUTO_INCREMENT,\n    username VARCHAR(255) NOT NULL UNIQUE,\n    email VARCHAR(255) NOT NULL UNIQUE\n);\n\n-- Objects table\nCREATE TABLE objects (\n    object_id INT PRIMARY KEY AUTO_INCREMENT,\n    name VARCHAR(255) NOT NULL,\n    description TEXT\n);\n\n-- Criteria table\nCREATE TABLE criteria (\n    criterion_id INT PRIMARY KEY AUTO_INCREMENT,\n    name VARCHAR(255) NOT NULL UNIQUE\n);\n\n-- Ratings table\nCREATE TABLE ratings (\n    rating_id INT PRIMARY KEY AUTO_INCREMENT,\n    user_id INT NOT NULL,\n    object_id INT NOT NULL,\n    criterion_id INT NOT NULL,\n    score INT CHECK (score BETWEEN 1 AND 5),  -- Assuming a 1-5 scale for ratings\n    rated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,\n    FOREIGN KEY (user_id) REFERENCES users(user_id),\n    FOREIGN KEY (object_id) REFERENCES objects(object_id),\n    FOREIGN KEY (criterion_id) REFERENCES criteria(criterion_id),\n    UNIQUE (user_id, object_id, criterion_id)  -- Ensures a user rates an object only once per criterion\n);