RESTORE DATABASE YourDatabaseName\n    FROM DISK = 'C:\first_part.bak'\n    WITH MOVE 'LogicalDataFileName' TO 'C:\path\to\YourDatabase.mdf',\n    MOVE 'LogicalLogFileName' TO 'C:\path\to\YourDatabase_log.ldf',\n    RECOVERY;\n    \n    -- Add additional parts if needed\n    RESTORE DATABASE YourDatabaseName\n    FROM DISK = 'C:\second_part.bak'\n    WITH NORECOVERY; -- Use RECOVERY for the final part to bring database online