SELECT t1.itemcode, t1.desc\nFROM (SELECT itemcode, desc, Count(desc) AS cnt\n      FROM yourtable\n      GROUP BY itemcode, desc) t1\nINNER JOIN (SELECT itemcode, Max(cnt) AS maxcnt\n            FROM (SELECT itemcode, desc, Count(desc) AS cnt\n                  FROM yourtable\n                  GROUP BY itemcode, desc) subquery\n            GROUP BY itemcode) t2\nON t1.itemcode = t2.itemcode AND t1.cnt = t2.maxcnt;