CREATE TRIGGER tr_YourTableName_UpdateLastModified ON YourTableName\nAFTER UPDATE\nAS\nBEGIN\n    SET NOCOUNT ON;\n    \n    IF UPDATE(LastModifiedDate)\n        RETURN; -- To avoid recursion if LastModifiedDate is updated again within the same trigger\n    \n    UPDATE T1\n    SET T1.LastModifiedDate = GETDATE()\n    FROM YourTableName AS T1\n    INNER JOIN inserted AS i \n    ON T1.ID = i.ID;\nEND\nGO