DECLARE @SearchString NVARCHAR(100) = N'misspelledWord'; -- The string you are searching for\nDECLARE @SQLQuery NVARCHAR(MAX) = N'';\n\n-- Build the dynamic SQL command\nSELECT @SQLQuery += \n    N'SELECT ''' + TABLE_NAME + '.' + COLUMN_NAME + ''' AS [Location], ' +\n    COLUMN_NAME + N' AS [Content] ' +\n    N'FROM [' + INFORMATION_SCHEMA.TABLES.TABLE_CATALOG + '].[' + TABLE_SCHEMA + '].[' + TABLE_NAME + '] ' +\n    N'WHERE ' + COLUMN_NAME + ' LIKE ''%' + @SearchString + '%''; '\nFROM INFORMATION_SCHEMA.COLUMNS\nJOIN INFORMATION_SCHEMA.TABLES ON INFORMATION_SCHEMA.COLUMNS.TABLE_NAME = INFORMATION_SCHEMA.TABLES.TABLE_NAME\nWHERE DATA_TYPE IN ('varchar', 'text')\nAND INFORMATION_SCHEMA.TABLES.TABLE_TYPE = 'BASE TABLE';\n\n-- Execute the SQL command\nEXEC sp_executesql @SQLQuery;