BEGIN TRANSACTION;\n\nSELECT TOP (1) WorkID, Payload\nINTO #TempWorkItems\nFROM YourTable WITH (UPDLOCK, ROWLOCK)\nWHERE Processed = 0\nORDER BY WorkID; -- or another column to define order\n\n-- Now process the rows in #TempWorkItems and update YourTable as needed\nUPDATE YourTable\nSET Processed = 1,\n    LastProcessedTime = GETDATE()\nWHERE WorkID IN (SELECT WorkID FROM #TempWorkItems);\n\nCOMMIT TRANSACTION;