CREATE TABLE base_actor (\n    id INT PRIMARY KEY AUTO_INCREMENT,\n    type ENUM('worker', 'employer', 'contact') NOT NULL,\n    common_attribute_1 VARCHAR(255),\n    common_attribute_2 VARCHAR(255),\n    worker_specific_attr VARCHAR(255),\n    employer_specific_attr VARCHAR(255)\n);\n\nCREATE TABLE communication (\n    id INT PRIMARY KEY AUTO_INCREMENT,\n    content TEXT,\n    base_actor_id INT,\n    FOREIGN KEY (base_actor_id) REFERENCES base_actor(id)\n);\n\nCREATE TABLE notes (\n    id INT PRIMARY KEY AUTO_INCREMENT,\n    note_content TEXT,\n    base_actor_id INT,\n    FOREIGN KEY (base_actor_id) REFERENCES base_actor(id)\n);