WITH RankedRows AS (\n    SELECT\n        id,\n        L1_name,\n        L2_name,\n        L3_name,\n        L4_name,\n        L5_name,\n        L6_name,\n        L7_name,\n        L8_name,\n        L9_name,\n        L10_name,\n        action,\n        ROW_NUMBER() OVER (PARTITION BY L11_data, action ORDER BY id) AS rn\n    FROM your_table_name\n)\nDELETE FROM RankedRows WHERE rn > 1;