SELECT tb.*\nFROM (\n    SELECT \n        vCodesWithPEs.PROD_PERM,\n        vDeriveAvailabilityFromPE.SomeColumn1, -- Replace with actual column name\n        PE_PDP.SomeColumn2                  -- Replace with actual column name\n        -- Add other necessary columns here\n    FROM vCodesWithPEs \n    INNER JOIN vDeriveAvailabilityFromPE ON vCodesWithPEs.PROD_PERM = vDeriveAvailabilityFromPE.PEID\n    INNER JOIN PE_PDP ON vCodesWithPEs.PROD_PERM = PE_PDP.PEID\n) AS tb;