CREATE TABLE votes (\n       id INT AUTO_INCREMENT PRIMARY KEY,\n       question_id INT NOT NULL,\n       voter_id INT NOT NULL,  -- To track who voted (optional but recommended)\n       answer ENUM('Yes', 'No', 'Maybe') NOT NULL,\n       weight TINYINT AS (CASE answer WHEN 'Yes' THEN 2 WHEN 'Maybe' THEN 1 ELSE 0 END) STORED,\n       created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP\n   );