SELECT \n    user_id, \n    user_name, \n    SUM(tpoints.point_points) AS total_points,\n    SUM(CASE WHEN MONTH(FROM_UNIXTIME(mpoints.point_date)) = MONTH(CURDATE()) AND YEAR(FROM_UNIXTIME(mpoints.point_date)) = YEAR(CURDATE())\n         THEN mpoints.point_points ELSE 0 END) AS month_points\nFROM \n    users \nLEFT JOIN \n    points tpoints ON users.user_id = tpoints.point_userid \nLEFT JOIN \n    points mpoints ON users.user_id = mpoints.point_userid\nWHERE \n    user_id = 1\nGROUP BY \n    user_id, user_name;