-- Drop all foreign key constraints\nDECLARE @name NVARCHAR(128)\nDECLARE @constraintCommand NVARCHAR(4000)\n\nDECLARE c CURSOR FOR\nSELECT \n    'ALTER TABLE [' + TABLE_SCHEMA + '].[' + TABLE_NAME + '] DROP CONSTRAINT [' + CONSTRAINT_NAME +']'\nFROM INFORMATION_SCHEMA.TABLE_CONSTRAINTS\nWHERE CONSTRAINT_TYPE = 'FOREIGN KEY'\n\nOPEN c\nFETCH NEXT FROM c INTO @constraintCommand\n\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    EXEC(@constraintCommand)\n    PRINT '-- Executed: ' + @constraintCommand\n    FETCH NEXT FROM c INTO @constraintCommand\nEND\nCLOSE c\nDEALLOCATE c\n\n-- Drop all primary key constraints\nDECLARE d CURSOR FOR\nSELECT \n    'ALTER TABLE [' + SCHEMA_NAME(t.schema_id) + '].[' + t.name + '] DROP CONSTRAINT [' + i.name +']'\nFROM sys.tables AS t\nINNER JOIN sys.key_constraints AS i ON \n     t.object_id = i.parent_object_id AND  \n     i.type_desc = N'PRIMARY_KEY_CONSTRAINT'\n\nOPEN d\nFETCH NEXT FROM d INTO @constraintCommand\n\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    EXEC(@constraintCommand)\n    PRINT '-- Executed: ' + @constraintCommand\n    FETCH NEXT FROM d INTO @constraintCommand\nEND\n\nCLOSE d\nDEALLOCATE d