USE YourDatabaseName;\nGO\n\n-- Create the login and user if they don't exist yet\nIF NOT EXISTS (SELECT 1 FROM sys.sql_logins WHERE name = 'AspNetMembershipUser')\nBEGIN\n    CREATE LOGIN AspNetMembershipUser WITH PASSWORD = 'YourStrongPassword';\nEND\nGO\n\n-- Create the user in the database\nCREATE USER AspNetMembershipUser FOR LOGIN AspNetMembershipUser;\nGO\n\n-- Grant necessary permissions\nGRANT SELECT ON dbo.aspnet_Users TO AspNetMembershipUser;\nGRANT INSERT ON dbo.aspnet_Users TO AspNetMembershipUser;\nGRANT UPDATE ON dbo.aspnet_Users TO AspNetMembershipUser;\nGRANT DELETE ON dbo.aspnet_Users TO AspNetMembershipUser;\n\n-- Repeat for other tables and roles as necessary\nGRANT SELECT, INSERT, UPDATE, DELETE ON dbo.aspnet_Membership TO AspNetMembershipUser;\nGRANT SELECT, INSERT, UPDATE, DELETE ON dbo.aspnet_Roles TO AspNetMembershipUser;\nGO