-- Inserting comment threads (example)\nINSERT INTO comment (article_id, user_id, comment, timestamp, "thread") VALUES (?, ?, ?, NOW(), '');\n\n-- Reply to a specific comment with ID 1\nINSERT INTO comment (article_id, user_id, comment, timestamp, "thread") VALUES (?, ?, ?, NOW(), '1');\n\n-- Reply to a nested comment with path '1-3'\nINSERT INTO comment (article_id, user_id, comment, timestamp, "thread") VALUES (?, ?, ?, NOW(), '1-3');