-- Creating an In-Memory Table Variable\nDECLARE @InMemoryTable TABLE (\n    ID INT PRIMARY KEY,\n    Name VARCHAR(50)\n);\n\nINSERT INTO @InMemoryTable (ID, Name) VALUES (1, 'John Doe'), (2, 'Jane Smith');\n\nSELECT * FROM @InMemoryTable;\n\n-- Creating a Temporary Table\nCREATE TABLE #TempTable (\n    ID INT PRIMARY KEY,\n    Name VARCHAR(50)\n);\n\nINSERT INTO #TempTable (ID, Name) VALUES (3, 'Alice Johnson'), (4, 'Bob Davis');\n\nSELECT * FROM #TempTable;