CREATE FUNCTION MyFunc (\n    @datePartIndicator INT,\n    @inputDate DATETIME\n)\nRETURNS INT\nAS\nBEGIN\n    DECLARE @returnValue INT;\n\n    SELECT @returnValue = CASE @datePartIndicator\n        WHEN 1 THEN DATEPART(dd, @inputDate)   -- Day of the month\n        WHEN 2 THEN DATEPART(mm, @inputDate)   -- Month\n        WHEN 3 THEN DATEPART(yy, @inputDate)   -- Year\n        WHEN 4 THEN DATEPART(hh, @inputDate)   -- Hour\n        ELSE NULL                              -- Invalid input\n    END;\n\n    RETURN @returnValue;\nEND;