-- Suppose you have this trigger on TableA that also inserts into TableB:\nCREATE TRIGGER trg_TableA_Insert ON TableA AFTER INSERT \nAS BEGIN\n    INSERT INTO TableB (Name) VALUES ('Trigger Insert');\nEND;\n\n-- And your main insert statement:\nINSERT INTO TableA (Name) VALUES ('Main Insert');\n\nSELECT SCOPE_IDENTITY(); -- This will return the ID from TableA inserted by 'Main Insert'\nSELECT IDENT_CURRENT('TableA');  -- Also returns the ID from TableA\nSELECT IDENT_CURRENT('TableB');  -- Returns the ID from TableB inserted by the trigger