SELECT \n    [System.WorkItemId], \n    [System.Title], \n    [System.Tags], \n    [Microsoft.VSTS.Common.Priority],\n    [Microsoft.VSTS.TCM.ReproSteps]\nFROM WorkItems\nWHERE \n    [System.TeamProject] = 'YourTeamProject' AND\n    [System.WorkItemType] = 'User Story'\nORDER BY [System.WorkItemId]\n\n-- To see bugs linked to specific User Stories, use this template:\nSELECT \n    [System.Id], \n    [System.Title],\n    [Microsoft.VSTS.Common.Priority]\nFROM WorkItems\nWHERE \n    [System.TeamProject] = 'YourTeamProject' AND\n    [System.WorkItemType] = 'Bug' AND\n    [System.HyperLinkCount] > 0 AND\n    [System.Links.Forward] CONTAINS 'User Story'\nORDER BY [System.Id]