WITH NumberedResults AS (\n    SELECT \n        YourColumns, \n        ROW_NUMBER() OVER (ORDER BY SomeColumn) AS RowNum,\n        COUNT(*) OVER () AS TotalCount -- Use COUNT(*) OVER () to get the total count in each row\n    FROM \n        YourLargeTable\n)\nSELECT \n    YourColumns, \n    TotalCount\nFROM \n    NumberedResults\nWHERE \n    RowNum BETWEEN @StartRow AND @EndRow;  -- Filter for paginatio