-- test.sql\nWHENEVER SQLERROR EXIT sql.sqlcode;\nWHENEVER OSERROR  EXIT FAILURE;\n\nVARIABLE userid     VARCHAR2(30);\nVARIABLE password   VARCHAR2(30);\nVARIABLE tablespace VARCHAR2(30);\n\nBEGIN\n    :userid     := '&1';\n    :password   := '&2';\n    :tablespace := '&3';\n\n    IF :userid IS NULL OR :userid = '' THEN\n        RAISE_APPLICATION_ERROR(-20001, 'User ID is missing');\n    END IF;\n\n    IF :password IS NULL OR :password = '' THEN\n        RAISE_APPLICATION_ERROR(-20001, 'Password is missing');\n    END IF;\n\n    IF :tablespace IS NULL OR :tablespace = '' THEN\n        RAISE_APPLICATION_ERROR(-20001, 'Tablespace is missing');\n    END IF;\n\n    -- Rest of your script\nEND;\n/