CREATE TABLE ClientSwitches (\n    ClientID INT PRIMARY KEY,\n    Switch1 BIT DEFAULT 0,\n    Switch2 BIT DEFAULT 0,\n    -- Continue for all switches up to Switch256\n    Switch256 BIT DEFAULT 0\n);\n\n-- Example of checking and setting a switch\nUPDATE ClientSwitches SET Switch1 = 1 WHERE ClientID = @ClientID;\nSELECT CASE WHEN Switch1 = 1 THEN 'Yes' ELSE 'No' END AS ShowSalaryOnChecks FROM ClientSwitches WHERE ClientID = @ClientID;