CREATE TABLE ClientSwitchConfig (\n    ConfigID INT IDENTITY(1,1) PRIMARY KEY,\n    ClientID INT NOT NULL,\n    SwitchName NVARCHAR(50) NOT NULL,\n    SwitchValue BIT DEFAULT 0,\n    UNIQUE (ClientID, SwitchName)\n);\n\n-- Example of checking and setting a switch\nINSERT INTO ClientSwitchConfig (ClientID, SwitchName, SwitchValue) VALUES (@ClientID, 'ShowSalaryOnChecks', 1) ON DUPLICATE KEY UPDATE SwitchValue = 1;\nSELECT CASE WHEN SwitchValue = 1 THEN 'Yes' ELSE 'No' END AS ShowSalaryOnChecks FROM ClientSwitchConfig WHERE ClientID = @ClientID AND SwitchName = 'ShowSalaryOnChecks';