SELECT 'Male' AS Lineage_Type, level AS Depth, a.Male_Animal_ID FROM (\n    SELECT animal_id AS Male_Animal_ID, parent_male_id AS Parent_Male_ID\n      FROM animal\n     START WITH animal_id = 123\n   CONNECT BY PRIOR animal_id = parent_male_id\n) a\n\nUNION ALL\n\nSELECT 'Female' AS Lineage_Type, level AS Depth, a.Female_Animal_ID \nFROM (\n    SELECT animal_id AS Female_Animal_ID, parent_female_id AS Parent_Female_ID\n      FROM animal\n     START WITH animal_id = 123\n   CONNECT BY PRIOR animal_id = parent_female_id\n) a;