DECLARE @UserInput NVARCHAR(255) = 'brown cow';\nDECLARE @WordCount INT;\n\n-- Split the user input into words and check presence in the field\nSELECT TOP 1 *\nFROM (\n    SELECT *,\n           (CASE WHEN CHARINDEX('brown', YourField) > 0 THEN 1 ELSE 0 END +\n            CASE WHEN CHARINDEX('cow', YourField) > 0 THEN 1 ELSE 0 END) AS MatchCount\n    FROM YourTable\n    WHERE YourField LIKE '%' + 'brown' + '%'\n       OR YourField LIKE '%' + 'cow' + '%'\n) AS Matches\nORDER BY MatchCount DESC;