-- Correcting the structure for item table (assuming it has 'id' as primary key)\nCREATE TABLE `item` (\n  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,\n  `name` varchar(255) NOT NULL,\n  PRIMARY KEY (`id`)\n) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;\n\n-- Correcting the structure for store table (assuming it has 'id' as primary key)\nCREATE TABLE `store` (\n  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,\n  `name` varchar(255) NOT NULL,\n  PRIMARY KEY (`id`)\n) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;\n\n-- Correcting the structure for link_item_store table with proper foreign keys\nCREATE TABLE `link_item_store` (\n  `fk_storeID` int(11) unsigned NOT NULL,\n  `fk_itemID` int(11) unsigned NOT NULL,\n  PRIMARY KEY (`fk_storeID`, `fk_itemID`),\n  FOREIGN KEY (`fk_storeID`) REFERENCES `store`(`id`) ON DELETE CASCADE,\n  FOREIGN KEY (`fk_itemID`) REFERENCES `item`(`id`) ON DELETE CASCADE\n) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;